    private void jButton12ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton12ActionPerformed
        if (getStart() == false) {
            jDialog6.dispose();
            FileNameExtensionFilter filter = new FileNameExtensionFilter("ARFF FILES", "arff", "arff");
            jFileChooser1.setFileFilter(filter);
            int returnVal = jFileChooser1.showOpenDialog(this);
            if (returnVal == JFileChooser.APPROVE_OPTION) {
                File file = jFileChooser1.getSelectedFile();
                try {
                    // What to do with the file, e.g. display it in a TextArea
                    clust.runTrainingSet(file.getAbsolutePath());
                } catch (Exception ex) {
                    Logger.getLogger(GUI.class.getName()).log(Level.SEVERE, null, ex);
                }
                System.out.println("Cluster retrained using: \n" + file.getAbsolutePath());
                jDialog8.setVisible(rootPaneCheckingEnabled);
                while (clust.getTrainingFlag() == false) {

                }
                clust.setTrainingFlag(false);
                jDialog8.dispose();
                jDialog9.setVisible(rootPaneCheckingEnabled);
            } else {
                System.out.println("File access cancelled by user.");
            }
        } else {
            jDialog7.setVisible(rootPaneCheckingEnabled);
        }

    }//GEN-LAST:event_jButton12ActionPerformed

