    /**
     * Converts the input matrix to a BufferedImage and returns it.
     * 
     * @param matrix
     * @return
     * @throws Exception
     */
    public static BufferedImage Mat2BufferedImage(Mat matrix) throws Exception {
        MatOfByte mob = new MatOfByte();
        Imgcodecs.imencode(".jpg", matrix, mob);
        byte ba[] = mob.toArray();

        BufferedImage bi = ImageIO.read(new ByteArrayInputStream(ba));
        return bi;
    }

    /**
     * Finds contours in the input matrix, draws the contours onto the color
     * matrix and returns the matrix.
     * @param input
     * @param color
     * @return
     */
    public Mat drawContour(Mat input, Mat color) {
        Mat contourImg = new Mat();
        List<MatOfPoint> contours = new ArrayList<>();
        Mat image = new Mat();
        input.convertTo(image, CvType.CV_32SC1);

        Imgproc.findContours(image, contours, new Mat(), Imgproc.RETR_FLOODFILL, Imgproc.CHAIN_APPROX_SIMPLE);

        contourImg = color.clone();
        for (int i = 0; i < contours.size(); i++) {
            Imgproc.drawContours(contourImg, contours, i, new Scalar(45, 170, 41), 1);
        }
        return contourImg;
    }

